    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int a = Integer.parseInt(jtfA.getText());
        int b = Integer.parseInt(jtfB.getText());
        int c = Integer.parseInt(jtfC.getText());
        
        double radicando = Math.pow(b, 2) - 4 * a * c;
        if (radicando < 0) {
            jlResultado.setText("Non hai soluciÃ³n real");
        } else {
            double x1 = (-b + Math.sqrt(radicando)) / (2 * a);
            double x2 = (-b - Math.sqrt(radicando)) / (2 * a);
            jlX1.setText(String.valueOf(x1));
            llX2.setText(String.valueOf(x2));
        }
    }//GEN-LAST:event_jButton1ActionPerformed

